    private void Accept2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Accept2ActionPerformed
        if(SegundosB.isSelected()){
            GSConverter segundo = new GSConverter();
            float seconds = 31536000;
            segundo.setSeconds(Float.parseFloat(ValorField.getText())*seconds);
            TotalField.setText(segundo.getSeconds()+" Segundos");
        }
        if(MinutosB.isSelected()){
            GSConverter minuto = new GSConverter();
            float minutes = 525600;
            minuto.setMinutes(Float.parseFloat(ValorField.getText())*minutes);
            TotalField.setText(minuto.getMinutes()+" Minutos");
        }
        else if(HorasB.isSelected()){
            GSConverter hora = new GSConverter();
            float hours = 8760;
            hora.setHours(Float.parseFloat(ValorField.getText())*hours);
            TotalField.setText(hora.getHours()+" Horas");
        }
        else if(DiasB.isSelected()){
            GSConverter dia = new GSConverter();
            float days = 365;
            dia.setDays(Float.parseFloat(ValorField.getText())*days);
            TotalField.setText(dia.getDays()+" Dias");
        }
        else if(SemanasB.isSelected()){
            GSConverter semana = new GSConverter();
            float weeks = 52;
            semana.setWeeks(Float.parseFloat(ValorField.getText())*weeks);
            TotalField.setText(semana.getWeeks()+" Semanas");
        }
        else if(MesesB.isSelected()){
            GSConverter mes = new GSConverter();
            float months = 12;
            mes.setMonths(Float.parseFloat(ValorField.getText())*months);
            TotalField.setText(mes.getMonths()+" Meses");
        }
    }//GEN-LAST:event_Accept2ActionPerformed

